    private void buttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonActionPerformed
        value = (String)jComboBox1.getSelectedItem();
        from = (String)combo2.getSelectedItem();
        to = (String)combo3.getSelectedItem();
        int t = 0;
        try{
            if(from == to)
                t = 2;
            if(value == null)
                t = 1;
        }
        finally{
            if(t == 2){
                label1.setText("Onset and Destination cannot be same");
                value = null;
            }
            else if(t == 1)
                label1.setText("Please select an option");
        }
            
        if("Volvo".equals(value)){
            new Volvo().setVisible(true);
            label1.setText("");
        }
                 
                   // TODO add your handling code here:
    }//GEN-LAST:event_buttonActionPerformed
    
